import React from 'react';
import { useState } from 'react';

function Pager({ elements = null }) {

    let pages = [];

    const [currentPagination, setPagination] = useState(0);

    pages = elements;

    let paginationNumbers = [];


    for (let x = currentPagination - 5; x < (currentPagination + 5); x++)
    {
        if (x === 0)
            break;

        paginationNumbers.push(
            <div onClick={() => GoNext(x)} class="paginationNumberBlock">
                <p>{x}</p>
            </div>
            )
        
    }


    return (
        <div class="pager" >
            <div class="currentPaginationElement">
                {
                    pages[currentPagination]
                }
            </div>
            <div class="paginationWrapper">
                {
                    paginationNumbers
                }
            </div>
        </div>
    );

    function GoNext(pagination) {
        setPagination(pagination)
    }

    function checkForLeftLimit(pagination) {

    
    }

    // returns last index in pages that is not null
    function checkForRightLimit(pagination) {
        for (x = pagination;pagination < pagination + 5; x++)
        {
            if (!pages[x]) {
                return x - 1;
            }
        }
    }
}


export default Pager;
