import React from 'react';
import { useState } from 'react';

function Pager({ elements = null }) {

    let pages = [];

    const [currentPagination, setPagination] = useState(0);

    pages = elements;

    let paginationNumbers = [];

    for (let x = currentPagination + 5; x > (currentPagination - 5); x--)
    {
        if (x === 0)
            break;

        paginationNumbers.push(
            <div onClick={setPagination(currentPagination+1)} class="paginationNumberBlock">
                <p>{x}</p>
            </div>
            )
        
    }


    return (
        <div class="pager" >
            <div class="currentPaginationElement">
                {
                    pages[currentPagination]
                }
            </div>
            <div class="paginationWrapper">
                {
                    paginationNumbers
                }
            </div>
        </div>
    );
}

export default Pager;
