import React from 'react';

let pages = [];
let currentPagination = 0;

function Pager({ elements = null }) {

    pages = elements;

    let paginationNumbers = [];

    for (let x = currentPagination + 5; x > (currentPagination - 5); x--)
    {
        if (x === 0)
            break;

        paginationNumbers.push(
            <div class="paginationNumberBlock">
                <p>{x}</p>
            </div>
            )
        
    }


    return (
        <div class="pager" >
            <div class="currentPaginationElement">
                {
                    pages[currentPagination]
                }
            </div>
            <div class="paginationWrapper">
                {
                    paginationNumbers
                }
            </div>
        </div>
    );
}

export default Pager;
